mapper.update:
update t_coupon
set coupon_stock = coupon_stock - 1
where coupon_id = #{couponId}
  and coupon_stock > 0;

update t_coupon
set coupon_stock = coupon_stock - #{batchSize}
where coupon_id = #{couponId}
  and coupon_stock >= #{batchSize}
  and del_flag = 0;

update t_coupon
set coupon_stock = coupon_stock + #{diff}
where coupon_id = #{couponId}
  and del_flag = 0;

update t_coupon
set coupon_stock = coupon_stock + #{back}
where coupon_id = #{couponId}
  and del_flag = 0;
